var dataMap = {};

function dataFormatter(obj) {
	var pList = ['四川', '河北', '内蒙古', '吉林', '上海', '浙江', '福建'];
	var temp;
	for (var year = 2009; year <= 2016; year++) {
		var max = 0;
		var sum = 0;
		temp = obj[year];
		for (var i = 0, l = temp.length; i < l; i++) {
			max = Math.max(max, temp[i]);
			sum += temp[i];
			obj[year][i] = {
				name: pList[i],
				value: temp[i]
			}
		}
		obj[year + 'max'] = Math.floor(max / 100) * 100;
		obj[year + 'sum'] = sum;
	}
	return obj;
}

