/**
 * 创建箭头的SVG路径。
 * @author Molay Chen, 大数据可视化部
 * @param {number} width 箭头宽度
 * @param {number} height 箭头高度
 * @param {number} curvature 内部曲率
 * @return {string} 箭头的SVG路径。
 */
function createArrowPath(width, height, curvature) {
    var ps = [
        0, height,
        width / 2, 0,
        width, height,
        width / 2, (1 - curvature) * height
    ];
    return [
        "M", ps[0], ps[1],
        "L", ps[2], ps[3],
        "L", ps[4], ps[5],
        "L", ps[6], ps[7],
        "L", ps[0], ps[1],
        "Z"
    ].join(" ");
}

/**
 * 转换连接信息为ECharts格式。
 * @author Molay Chen, 大数据可视化部
 * @param {array} links 待转换的连接数组
 * @return {array} 转换后的数组
 */
function convertLinksData(links) {
    var data = links.map(function(item, i) {
        var sourceCoord = geoCoordMap[item.source];
        if (!sourceCoord)
            throw new Error("找不到“" + item.source + "”的坐标，请检查坐标信息。");
        var targetCoord = geoCoordMap[item.target];
        if (!targetCoord)
            throw new Error("找不到“" + item.target + "”的坐标，请检查坐标信息。");
        return {
            fromName: item.source,
            toName: item.target,
            coords: [sourceCoord, targetCoord]
        };
    });
    return data;
}

/**
 * 转换连接信息为ECharts的节点格式。
 * @author Molay Chen, 大数据可视化部
 * @param {array} links 待转换的连接数组
 * @return {array} 转换后的节点数组
 */
function convertNodesData(links) {
    var map = {};
    var data = [];

    function addNode(name, coord, value) {
        if (map[name]) return;
        if (!coord)
            throw new Error("找不到“" + name + "”的坐标，请检查坐标信息。");
        map[name] = {
            name: name,
            value: coord.concat(value)
        };
        data.push(map[name]);
    }
    links.forEach(function(item, i) {
        addNode(item.source, geoCoordMap[item.source], 50);
        addNode(item.target, geoCoordMap[item.target], 50);
    });
    return data;
}

